mapper.select:
SELECT (SELECT m.MEM_NAME FROM ITM_MEMBER m WHERE l.REG_IDX = m.MEM_IDX ) AS memName, l.REG_DATE as regDate, a.ASS_ULID AS assUlid, a.ASS_NAME AS assName, l.AL_TYPE AS alType, l.AL_CAT AS alCat, l.AL_CONT AS alCont, l.AL_NOTE AS alNote FROM ITM_ASS_LOG l LEFT JOIN ITM_ASSET a ON a.ASS_IDX = l.ASS_IDX
ORDER BY AL_IDX ASC ORDER BY AL_IDX DESC
LIMIT #{startList}, #{listSize}
