mapper.update:
UPDATE ITM_ASSET_CATEGORY SET
ASS_CAT_NAME = #{assCatName}, ASS_CAT_CODE = #{assCatCode}, MOD_DATE = NOW(), MOD_IDX = #{modIdx}, SL_NOTE = #{slNote}
WHERE ASS_CAT_IDX = #{assCatIdx}

UPDATE ITM_ASSET_CATEGORY SET
DEL_YN = 'Y', DEL_DATE = NOW(), DEL_IDX = #{delIdx}, SL_NOTE = #{slNote}
WHERE ASS_CAT_IDX = #{assCatIdx}
